// list of numbers from 1 to 10
numbers = 1:10

// variable to sum numbers
sum = 0

// loop variable
i = 1

// while the variable i is less or equal to the size of numbers
while i <= $numbers

// add the value of numbers at index i to sum and increment i by 1
sum += numbers(i++)

end

// output the value of sum
sum

// Stock symbol for Apple
symbol = "AAPL"

// Date for 3 months ago
startDate = date("y-0m-0d", -3@months)

// The "<<" operator is used for string concatenation
url = "query.yahooapis.com/v1/public/yql?q=select * from yahoo.finance.historicaldata where symbol='" << symbol << "' and startDate='" << startDate << "' and endDate='today'&format=json&diagnostics=true&env=http://datatables.org/alltables.env"

stocks = http(url, filter=[Close,Date])

// stock data from url
stocks = http("query.yahooapis.com/v1/public/yql?q=select * from yahoo.finance.historicaldata where symbol='YHOO' and startDate='" << date("y-0m-0d", -6@months) << "' and endDate='today'&format=json&diagnostics=true&env=http://datatables.org/alltables.env", filter=[Close,Date])

// plot prices
ListPlot(stocks(query, results, quote, *, Close), labels=date(stocks(query, results, quote, *, Date), "M3 d"), width=600, height=300, color=purple, gridDash=[10,2], grid=[5,2], numbers=[5,2], ticks=[1,1], flipAxis=1)
